require('@babel/register')({
    presets: ['@babel/preset-env']
  });
const {
    src,
    dest,
    series,
    watch
} = require('gulp');

const gulp = require('gulp');
const autoprefixes = require('gulp-autoprefixer');
const cleanCSS = require('gulp-clean-css');
const del = require('del');
const concat = require('gulp-concat');
const htmlMin = require('gulp-htmlmin');
const browserSync = require('browser-sync').create();
const svgSprite = require('gulp-svg-sprite');
const svgmin = require('gulp-svgmin');
const cheerio = require('gulp-cheerio');
const replace = require('gulp-replace');
const image = require('gulp-image');
const fileInclude = require('gulp-file-include');
const gulpif = require('gulp-if');
const uglify = require('gulp-uglify-es').default;
const babel = require('gulp-babel');
const notify = require('gulp-notify');
const fonter = require('gulp-fonter');
const ttf2woff2 = require('gulp-ttf2woff2');
const ttf2woff = require('gulp-ttf2woff');
const plumber = require('gulp-plumber');
const sourcemaps = require('gulp-sourcemaps');
const Typograf = require('typograf');
const rev = require('gulp-rev');
const revRewrite = require('gulp-rev-rewrite');
const revDel = require('rev-del');
const { readFileSync } = require('fs');

const typograf = new Typograf({ locale: ['ru'] });
let isProd = false; // Default: development

// Clean the app directory
const clean = () => del(['app']);

// Process fonts
const fonts = () => {
    return src('src/fonts/*')
        .pipe(fonter({
            formats: ['woff', 'woff2', 'ttf']
        }))
        .pipe(ttf2woff2())
        .pipe(ttf2woff())
        .pipe(dest('app/fonts'));
};

// Create SVG sprites
const svgSprites = () => {
    return src('src/images/svg/**/*.svg')
        .pipe(svgmin({ js2svg: { pretty: true } }))
        .pipe(cheerio({
            run: ($) => {
                $('[fill]').removeAttr('fill');
                $('[stroke]').removeAttr('stroke');
                $('[style]').removeAttr('style');
            },
            parserOptions: { xmlMode: true }
        }))
        .pipe(replace('&gt;', '>'))
        .pipe(svgSprite({
            mode: {
                symbol: {
                    sprite: "../sprite.svg",
                    example: true,
                    render: { scss: { dest: '../sprite.scss' } }
                }
            }
        }))
        .pipe(dest('app/img'));
};

// Optimize images
const images = () => {
    return src([
            'src/images/**/*.jpg',
            'src/images/**/*.jpeg',
            'src/images/**/*.png',
            'src/images/*.svg',
            'src/images/*.gif'
        ])
        .pipe(image())
        .pipe(dest('app/img'));
};

// Process styles
const styles = () => {
    return src('src/styles/**/*.css')
        .pipe(sourcemaps.init())
        .pipe(concat('main.css'))
        .pipe(autoprefixes({ cascade: false }))
        .pipe(cleanCSS({ level: 2 }))
        .pipe(dest('app'))
        .pipe(sourcemaps.write())
        .pipe(browserSync.stream());
};

// Process backend styles (if different from frontend styles)
const backendStyles = () => {
    return src('src/styles/**/*.css')
        .pipe(concat('main.css'))
        .pipe(autoprefixes({ cascade: false }))
        .pipe(cleanCSS({ level: 2 }))
        .pipe(dest('app'))
        .pipe(browserSync.stream());
};

// Process scripts
const scripts = () => {
    return src([
            'src/js/components/**/*.js',
            'src/js/main.js'
        ])
        .pipe(sourcemaps.init())
        .pipe(babel({ presets: ['@babel/env'] }))
        .pipe(concat('app.js'))
        .pipe(uglify({ toplevel: true }).on('error', notify.onError()))
        .pipe(sourcemaps.write())
        .pipe(dest('app'))
        .pipe(browserSync.stream());
};

// Process backend scripts (if different from frontend scripts)
const backendScripts = () => {
    return src([
            'src/js/components/**/*.js',
            'src/js/main.js'
        ])
        .pipe(babel({ presets: ['@babel/env'] }))
        .pipe(concat('app.js'))
        .pipe(uglify({ toplevel: true }).on('error', notify.onError()))
        .pipe(dest('app'))
        .pipe(browserSync.stream());
};

// Minify HTML
const htmlMinify = () => {
    return src('src/**/*.html')
        .pipe(htmlMin({ collapseWhitespace: true }))
        .pipe(dest('app'))
        .pipe(browserSync.stream());
};

// Include HTML files
const htmlInclude = () => {
    return src('src/**/*.html')
        .pipe(plumber())
        .pipe(fileInclude({ prefix: '@', basepath: '@file' }))
        .pipe(htmlMin({ collapseWhitespace: true }))
        .pipe(dest('app'))
        .pipe(browserSync.stream());
};

// Copy resources
const resources = () => {
    return src('src/resources/**')
        .pipe(dest('app'));
};

// Cache-busting
const cache = () => {
    return src('app/**/*.{css,js,svg,png,jpg,jpeg,webp,woff2}', { base: 'app' })
        .pipe(revAll.revision())
        .pipe(dest('app'))
        .pipe(revAll.manifest({ path: 'rev.json' }))
        .pipe(dest('app'));
};

// Rewrite cached files
const rewrite = () => {
    const manifest = readFileSync('app/rev.json');
    return src('app/**/*.{css,html}')
        .pipe(revRewrite({ manifest }))
        .pipe(dest('app'));
};

// Watch for file changes and reload
const watchFiles = () => {
    browserSync.init({ server: { baseDir: 'app' } });
    watch('src/**/*.html', htmlMinify);
    watch('src/**/*.html', htmlInclude);
    watch('src/**/*.css', styles);
    watch('src/**/*.js', scripts);
    watch('src/images/svg/**/*.svg', svgSprites);
    watch('src/resources/**', resources);
};

// Set production mode
const toProd = (done) => {
    isProd = true;
    done();
};

exports.styles = styles;
exports.scripts = scripts;
exports.htmlMinify = htmlMinify;
exports.svgSprites = svgSprites;
exports.fonts = fonts;
exports.clean = clean;

exports.default = series(clean, htmlMinify, fonts, styles, scripts, resources, images, svgSprites, watchFiles);
exports.backend = series(clean, fonts, htmlInclude, backendScripts, backendStyles, resources, images, svgSprites);
exports.build = series(toProd, clean, htmlInclude, fonts, scripts, styles, resources, images, svgSprites, htmlMinify);
exports.cache = series(cache, rewrite);
